//Assets
import DashWithStar from "../../img/DashWithStar.svg";
import {motion} from "framer-motion";
import {useState} from 'react';

//Daily Tasks
export default function Daily(props){
    return(
        <div className='Daily'>
                <h1>DAILY</h1>

                <div className='DailyList'>
                    {(props.array).map((item, index) => 
                        <TaskItem isDone={item.isDone} taskString={item.taskString} taskTime={item.taskTime} key={index} index={index} array={props.array}/>    
                    )}

                    <img src={DashWithStar} className='DashWithStar' alt=""/>
                </div>
        </div>
    )
}

function TaskItem(props){
    const [checkSymbol, updateSymbol] = useState("-");

    function markCheck(){
        if(props.array[props.index].isDone === true) props.array[props.index].isDone = false;
        else props.array[props.index].isDone = true;
        localStorage.setItem("Daily", JSON.stringify(props.array));
    }

    return(
        <div className="DailyItem">
            <motion.button className="Checkmark" whileHover={{scale:1.1}} onClick={markCheck}></motion.button>
            {props.taskString}
        </div>
            
    )
}
