//REACT
import { useState } from 'react';
import {transition_left} from '../../transition.js';
import {motion, AnimatePresence} from "framer-motion";
import "../css/DailyTasks.css"

//Components
import Header from "./Header.jsx";

//Assets
import WavesBottom from "../img/WavesBottom.svg";
import TaskSettings from "../img/TaskSettings.svg";
import DashWithStar from "../img/DashWithStar.svg";


if(!localStorage.getItem("Daily")){
    localStorage.setItem("Daily", "[]")
}


/* -----    APP    ----- */
function TasksPage(){
    let [dailyArray, updateDaily] = useState(JSON.parse(localStorage.getItem("Daily")));
    const [modalActive, setModalActive] = useState(false);
    const toggleModal = () => {setModalActive(!modalActive)};

    return(
        <>
            <Header/>
            <Daily array={dailyArray}/>
            <Weekly/>
            <Footer toggleModal={toggleModal}/>      

            <AnimatePresence>
                {modalActive && <SettingsModal toggleModal={toggleModal} dailyArray={dailyArray} updateDaily={updateDaily}/>}
            </AnimatePresence>
            <motion.button whileHover={{scale:1.1}}  className='Settings' whileTap={{scale:0.9}} onClick={toggleModal}>
                    <img src={TaskSettings} alt=""/>
            </motion.button>
            
        </>
    )
}

export default transition_left(TasksPage);

/* ----- COMPONENTS ----- */
//HEADER


//FOOTER
function Footer(){
    return(
        <div className='Footer'>
            <img src={WavesBottom} className='WavesBottom' alt=""/>
        </div>
    )
}

//Daily Tasks
function Daily(props){
    return(
        <div className='Daily'>
                <h1>DAILY</h1>

                <div className='DailyList'>
                    {(props.array).map((item, index) => 
                        <DailyTaskItem isDone={item.isDone} taskString={item.taskString} taskTime={item.taskTime} key={index} index={index} array={props.array}/>    
                    )}

                    <img src={DashWithStar} className='DashWithStar' alt=""/>
                </div>
        </div>
    )
}
function DailyTaskItem(props){
    function markCheck(){
        if(props.array[props.index].isDone === true) props.array[props.index].isDone = false;
        else props.array[props.index].isDone = true;
    }

    return(
        <label>
            <input type="checkbox" className='Checkbox' defaultChecked={props.isDone} onChange={markCheck}/>
            {props.taskString} {props.taskTime}
        </label>
    )
}

//Weekly Tasks
function Weekly(){
    return(
        <div className='Weekly'>
            <h2>Weekly Tasks</h2>
            <div className='WeeklyList'>
                <label>
                    Lorem Ipsum Lorem Ipsum
                    <input type='checkbox'/>
                </label>
            </div>
        </div>
    )
}


function SettingsModal(props){

    const [input, updateInput] = useState('');

    function addToList(E){
        E.preventDefault()
        props.addToDaily([...props.dailyArray, {"taskString":input, "isDone":false}]);
    }

    return(
        <motion.div className='Backdrop'
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
        >
            <div className='SettingsModal'>
                <h2>Your task list</h2>
                <div className='modalTypes'>
                    <motion.button whileTap={{scale:0.9}} className='modalButtonInactive'>Daily</motion.button>
                    <motion.button whileTap={{scale:0.9}} className='modalButtonInactive'>Weekly</motion.button>
                </div>
                
                <section className='modalTaskList'>
                    {props.dailyArray.map((item) =>
                        <div key='taskString'>
                            <motion.button whileHover={{scale:1.1}} className='x'>✖</motion.button>
                            <p>{item.taskString}</p>
                        </div>
                    )}
                </section>

                <form className='modalInputTask'>
                    <input placeholder='ADD NEW TASK' onChange={I => updateInput(I.target.value)}/>
                    <motion.button onClick={addToList}>✦</motion.button>
                </form>
            </div>
        </motion.div>
    )
}

//Other
