//Modules
import { useState } from 'react';
import {motion, AnimatePresence} from "framer-motion";

export default function SettingsModal(props){
    const [input, updateInput] = useState('');

    function addToList(E){
        E.preventDefault()
        props.addToDaily([...props.dailyArray, {"taskString":input, "isDone":false}]);
    }

    return(
        <motion.div className='Backdrop'
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
        >
            <div className='SettingsModal'>
                <h2>Your task list</h2>
                <div className='modalTypes'>
                    <motion.button whileTap={{scale:0.9}} className='modalButtonInactive'>Daily</motion.button>
                    <motion.button whileTap={{scale:0.9}} className='modalButtonInactive'>Weekly</motion.button>
                </div>
                
                <section className='modalTaskList'>
                    {props.dailyArray.map((item) =>
                        <div key='taskString'>
                            <motion.button whileHover={{scale:1.1}} className='x'>✖</motion.button>
                            <p>{item.taskString}</p>
                        </div>
                    )}
                </section>

                <form className='modalInputTask'>
                    <input placeholder='ADD NEW TASK' onChange={I => updateInput(I.target.value)}/>
                    <motion.button onClick={addToList}>✦</motion.button>
                </form>
            </div>
        </motion.div>
    )
}
