//MODULES
import { useState, useRef, useEffect} from 'react';
import {Link } from "react-router-dom";
import runCompletion from '../modules/ChatGPT.js';
import './Chat.css';

//ASSETS
import UserBubble from '../img/user-bubble.svg';
import MumisBubble from '../img/mumis-bubble.png';
import Star from '../img/Star.svg';
import Hourglass from '../img/hourglass.svg';
import backButton from '../img/back-button.svg';


function Chat() {
  //CHAT
  const [input, setInput] = useState("");
  const [iDisabled, setiDisabled] = useState(false);
  const [chatLog, setChatLog] = useState([{txt: "What's up?", img: MumisBubble}]);
  const [ThinkingStatus, SetThinking] = useState("thinking-off");
  const [ButtonImage, SetButtonImage] = useState(Star);

  //CHAT TO BOTTOM
  const bottomRef = useRef(null);
  useEffect(() => {bottomRef.current?.scrollIntoView({behavior: 'smooth'})}, [chatLog]);


  async function handleSubmit(event){
    event.preventDefault()
    setChatLog([...chatLog, {txt:`${input}`, img:UserBubble}])
    setInput("")
    setiDisabled(true);
    SetButtonImage(Hourglass);
    SetThinking("thinking-on");
    var output = await runCompletion(input);
    SetThinking("thinking-off"); 
    SetButtonImage(Star);
    setiDisabled(false);
    setChatLog([...chatLog, {txt:`${input}`, img:UserBubble}, {txt:`${output}`, img:MumisBubble}])
  }

  //FORMATTING
  return (
    <div className='Chat'>
      <div className='Header'>
      <Link to={`/`}>
        <button className='back-button'>
          <img src={backButton}/>
        </button>
      </Link>
        <h1>Let's Chat!</h1>
      </div>

      <div className='ChatField'>
        {chatLog.map((message, index) => (
          <Textline name={message.name} img={message.img} txt={message.txt} key={index}/>
        ))}
        <div ref={bottomRef}/>
      </div>

      <form className='ChatBox' onSubmit={handleSubmit}>
        <input 
          value={input}
          onChange={(e) => {setInput(e.target.value)}}
          required
          disabled = {iDisabled}
          autoFocus
        />
        <button type="submit" className="btn">
          <img src={Star} alt="Send" className='ButtonStar'/>
        </button>
      </form>

      <img src={Thinking} className={ThinkingStatus} alt=""/>
  </div>
  )
}

const Textline = ({img, txt}) => {
  return (
      <div className="Chatline">
          <img src={img} alt=""/>
          <div className='ChatlineText'>
            <p>{txt}</p>
          </div>

      </div>
  )
}

export default Chat
